$(document).ready(function(){

    var $monthcol = $('<div class="form-group col-md-5"></div>');
    var $monthsel = $('<select class="form-control month" name="month"></select>');
    
    $monthsel.append('<option selected="selected" value="Month">Month</option>');
    $monthsel.append('<option value="01">January(1)</option>');
    $monthsel.append('<option value="02">February(2)</option>');
    $monthsel.append('<option value="03">March(3)</option>');
    $monthsel.append('<option value="04">April(4)</option>');
    $monthsel.append('<option value="05">May(5)</option>');
    $monthsel.append('<option value="06">June(6)</option>');
    $monthsel.append('<option value="07">July(7)</option>');
    $monthsel.append('<option value="08">August(8)</option>');
    $monthsel.append('<option value="09">September(9)</option>');
    $monthsel.append('<option value="10">October(10)</option>');
    $monthsel.append('<option value="11">November(11)</option>');
    $monthsel.append('<option value="12">December(12)</option>');

    $monthcol.append($monthsel);


    var $daycol = $('<div class="form-group col-md-3"></div>');
    var $daysel = $('<select class="form-control day" name="day"></select>');

    $daysel.append('<option selected="selected" value="Day">Day</option>');
    for(var i = 1; i < 32; i++)
    {
        if(i < 10)
            $daysel.append('<option value="0' + i + '">0' + i + '</option>');
        else
            $daysel.append('<option value="' + i + '">' + i + '</option>');
    }

    $daycol.append($daysel);

    var $yearcol = $('<div class="form-group col-md-4"></div>');
    var $yearsel = $('<select class="form-control year" name="year"></select>');

    var d = new Date();
    var year = d.getFullYear()
    var i = year;
    if($('.date-picker').hasClass('dob-picker'))
    {
        year = year - 15;
        var i = year - 70;
    }        
    if($('.date-picker').hasClass('track'))
    {        
        var i = 2014;
    }            

    $yearsel.append('<option selected="selected" value="Year">Year</option>');
    for(i; i <= year; i++)
    {        
        $yearsel.append('<option value="' + i + '">' + i + '</option>');
    }

    $yearcol.append($yearsel);


    $('.date-picker').append($monthcol);
    $('.date-picker').append($daycol);
    $('.date-picker').append($yearcol);        
});